INSERT INTO `hot_show_comment` VALUES ('2016.05.23', '2026', '13123933', 'comment/16/5/7cf70260d8d34daeb217b5767e6ad6e4.jpg,comment/16/5/39cddcc0919c46aaad8eedd491718a23.jpg', '#象印#烧水壶，4L容量放办公室用足够，煮水静音，特别是冲挂耳咖啡，太方便了。用了几天感觉质量很好，烧水时几乎没声音，烧开后还有好听的音乐。设的90度保温，用的纯净水，健康养生，一天部门6个人够用了，还不会二次沸腾。关键是第二天大早就有热水喝，冲咖啡泡麦片，节省了好多时间。信任象印这牌子，价格也不贵。', 'wx248168630317', '4', 'https://mpic.secooimg.com/images/2016/05/05/img_information_shenmi.png');
