INSERT INTO `hot_show_comment` VALUES ('2016.05.23', '2036', '14623157', 'comment/16/5/e49682544bb94cbe8a20cbf9c56aefdf.jpg', '＃CHIARA FERRAGNI平底鞋＃穿着新款，感觉太好了。没人和你撞款，我就闪亮的一身出门了，小伙伴都惊呆了，都问我要鞋子的链接～ps：真的很不想给哈。说说穿着的体会吧。真的很舒服，这是我觉得超值的。好多鞋都是中看但不禁穿，把脚磨的生疼，这双完全不会啊。', '她', '8', 'http://pic.secooimg.com/headImage/16/5/8b72041cbf7442f58d3d5f4f3532c23a.jpg');
